'  ELSE to_char((LT- (MIN(LT) OVER () ))/1000, ''999.999'') END as gap,',
' CASE WHEN NVL((LT - lag(lt,1) OVER (ORDER BY lt))/1000, 0)  = 0 THEN  ''''',
'  ELSE to_char(NVL((LT - lag(lt,1) OVER (ORDER BY lt))/1000, 0), ''999.999'') END as interval, ',
